const Header = (props) => {
  return (
    <>
      <h1>{props.course}</h1>
    </>
  )
}

const Part = (props) => {
  return (
    <>
      <p>{props.name} {props.exercises}</p>
    </>
  )
}

const Content = (props) => {
  return(
  <>
    <Part part={props[0]} />
    <Part part={props[1]} />
    <Part part={props[2]} />
  </>
  )
}

const Total = (props) => {
  return (
    <>
      <p>
        Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises}
      </p>
    </>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
    name: 'Fundamentals of React',
    exercises: 10
    },
    {
    name: 'Using props to pass data',
    exercises: 7
    },
    {
    name: 'State of a component',
    exercises: 14
    }
  ]

  return (
    <>
      <Header course={course} />
      <Content parts={parts} />
      <Total parts={parts} />

    </>
  )
}

export default App
